import React from 'react';
import { Book, BookList } from '../types/nyt';

interface ItemListProps {
    lists: BookList[];
    onSelect: (book: Book) => void;
}

const ItemList: React.FC<ItemListProps> = ({ lists, onSelect }) => {
    if (!lists.length) {
        return <div>No books available</div>;
    }
    return (
        <div>
            {lists.map(list => (
                <div key={list.list_id} style={{marginBottom: '2rem'}}>
                    <h2>{list.display_name}</h2>
                    <ul>
                        {list.books.map(book => (
                            <li key={book.primary_isbn13} onClick={() => onSelect(book)}>
                                <img src={book.book_image} alt={book.title} width={100} />
                                <div>
                                    <p><strong>{book.title}</strong></p>
                                    <p>{book.author}</p>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
};

export default ItemList;
