import axios from 'axios';

const NYT_API_KEY = process.env.REACT_APP_NYT_API_KEY || '';
const NYT_API_URL = 'https://api.nytimes.com/svc/topstories/v2/home.json';

export const fetchArticles = async (): Promise<any[]> => {
    try {
        const response = await axios.get(NYT_API_URL, {
            params: {
                'api-key': NYT_API_KEY,
            },
        });
        return response.data.results;
    } catch (error) {
        console.error('Error fetching articles from NYT:', error);
        throw error;
    }
};

export const fetchArticleById = async (id: string): Promise<any | null> => {
    // NYT Top Stories API does not provide direct lookup by ID, so this is a mock implementation
    const articles = await fetchArticles();
    return articles.find((article: any) => article.uri === id) || null;
};
