import React, { useEffect, useState } from 'react';
import { fetchBookLists } from '../api/nyt';
import ItemList from '../components/ItemList';
import { Book, BookList } from '../types/nyt';
import { useHistory } from 'react-router-dom';

const ListPage: React.FC = () => {
    const [lists, setLists] = useState<BookList[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const history = useHistory();

    useEffect(() => {
        const loadLists = async () => {
            try {
                const data = await fetchBookLists();
                setLists(data);
            } catch (err) {
                setError('Failed to fetch book lists');
            } finally {
                setLoading(false);
            }
        };

        loadLists();
    }, []);

    const handleSelect = (book: Book) => {
        history.push(`/detail/${encodeURIComponent(book.primary_isbn13)}`);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>New York Times Best Sellers</h1>
            <ItemList lists={lists} onSelect={handleSelect} />
        </div>
    );
};

export default ListPage;
