import React from 'react';
import { useParams, useHistory } from 'react-router-dom';
import ItemDetail from '../components/ItemDetail';
import { fetchBookByIsbn } from '../api/nyt';
import { Book } from '../types/nyt';

const DetailPage: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [book, setBook] = React.useState<Book | null>(null);
    const [loading, setLoading] = React.useState<boolean>(true);
    const [error, setError] = React.useState<string | null>(null);
    const history = useHistory();

    React.useEffect(() => {
        const getBook = async () => {
            try {
                const data = await fetchBookByIsbn(decodeURIComponent(id));
                setBook(data);
            } catch (err) {
                setError('Failed to fetch book details');
            } finally {
                setLoading(false);
            }
        };
        getBook();
    }, [id]);

    if (loading) {
        return <div>Loading...</div>;
    }
    if (error) {
        return <div>{error}</div>;
    }
    if (!book) {
        return <div>No book found</div>;
    }
    return (
        <div>
            <button onClick={() => history.goBack()}>Back</button>
            <h1>Book Details</h1>
            <ItemDetail book={book} />
            <div style={{marginTop: '1rem'}}>
                <p><strong>ISBN-13:</strong> {book.primary_isbn13}</p>
                <p><strong>Publisher:</strong> {book.publisher}</p>
                <p><strong>Description:</strong> {book.description}</p>
                <a href={book.amazon_product_url} target="_blank" rel="noopener noreferrer">View on Amazon</a>
            </div>
        </div>
    );
};

export default DetailPage;
